-- Requisitos ---------------------------------------
-- Data de cadastro da proposta
-- Data da licitação
-- UASG / órgão
-- Cidade/Estado da UASG
-- Item (descrição)
-- Valor da proposta
-- Se participou ou não
-- Classificação (caso tenha) (não é obrigatório)
-- E os campos que conseguirem incluir na pesquisa...
-----------------------------------------------------
SELECT DISTINCT
	pp.prpCNPJ 						"CNPJ",
	pp.prpRazaoSocial 				"Razão Social",
	pp.prpData						"Data da Proposta",
	p.coduasg						"UASG",
	u.nomuasg						"Nome da UASG",
	o.NomeOrgao						"Órgão",
	TRIM(m.nommun)					"Município",
	TRIM(u.uf)						"UF",
	'Pregão' 						"Modalidade",
	p.numprp						"Numero da Licitação",
	pi.ipgItem						"Item da Licitação",
	pi.ipgNomeItem 					"Descrição do Item",
	pri.ippValor					"Valor da Proposta",
	pi.ipgValorRef					"Valor Estimado do Item",
	p.prgDataInclusao				"Data da Inclusão da Licitação",
	p.prgDataAbertura 				"Data da Abertura da Licitação",
	CASE pri.ippIndAdjudicado
		WHEN 'S' THEN 'Adjudicado'
		ELSE 'Não Adjudicado' END	"Indicador de Adjudicação"
FROM Comprasnet_VBL.tbl_Pregao p
JOIN Comprasnet_VBL.tbl_pregaoitem pi ON pi.prgCod = p.prgCod
JOIN Comprasnet_VBL.tbl_Proposta pp ON pp.prgCod = p.prgCod
JOIN Comprasnet_VBL.tbl_PropostaItem pri ON pri.prpCod = pp.prpCod
JOIN Comprasnet_VBL.tb_uasg u ON u.coduasg = p.coduasg
JOIN Comprasnet_VBL.tb_municipio m ON m.codmun = u.CodMunic
JOIN Comprasnet_VBL.tb_orgao o ON o.CodOrgao = u.CodOrgao
WHERE 1=1
	AND p.prgDataInclusao > '2023-08-27'
	AND pp.prpCNPJ IN ('26427482000154')
	AND pi.ipgItem > 0
;

